mapper.select:
SELECT id_area_estrategica, area_estrategica, id_estado, fec_registro, fec_modificacion, ult_usuario,gestion,codigo
FROM public.tareas_estrategia
WHERE id_area_estrategica=#{id_area_estrategica} and id_estado='A';

SELECT id_area_estrategica, area_estrategica, id_estado, fec_registro, fec_modificacion, ult_usuario, gestion,codigo
FROM public.tareas_estrategia
WHERE id_area_estrategica=#{id_area_estrategica} limit 1;

SELECT id_area_estrategica, area_estrategica, id_estado, fec_registro, fec_modificacion, ult_usuario,gestion,codigo
FROM public.tareas_estrategia
WHERE id_area_estrategica=#{id_area_estrategica} and id_estado='A';

SELECT id_area_estrategica, area_estrategica,gestion,codigo
FROM tareas_estrategia
WHERE id_estado='A' and gestion=#{gestion}

SELECT id_area_estrategica, area_estrategica,gestion,codigo
FROM tareas_estrategia
WHERE id_estado='A' and codigo=#{codigo}
