mapper.select:
SELECT id_configuracion, clave, valor, etiqueta, tipo_valor, id_estado
FROM public.tconfiguracion
WHERE id_estado = 'A' AND clave=#{clave}

mapper.update:
UPDATE public.tconfiguracion
SET valor=#{valor}
WHERE clave=#{clave}

mapper.select:
SELECT  valor
FROM public.tconfiguracion
WHERE clave=#{clave}
