mapper.select:
SELECT id_detalle_periodos_programacion, id_plan_pei, descripcion, id_estado, fec_registro, fec_modificacion, ult_usuario
FROM public.detalle_periodos_programacion
WHERE id_indicador_estrategico=#{id_detalle_periodos_programacion} and id_estado='A';

SELECT id_detalle_periodos_programacion,te.descripcion ,descripcion
FROM detalle_periodos_programacion td
         INNER JOIN public.plan_pei te ON td.id_plan_pei=te.id_plan_pei
WHERE td.id_estado='A' AND upper(te.descripcion) like ${buscar}
ORDER BY te.descripcion
    LIMIT ${pagina} OFFSET ${nro_pagina};

SELECT id_detalle_periodos_programacion,te.descripcion ,descripcion
FROM detalle_periodos_programacion td
         INNER JOIN public.plan_pei te ON td.id_plan_pei=te.id_plan_pei
WHERE td.id_estado='A' AND upper(descripcion) like ${buscar}
ORDER BY te.descripcion
    LIMIT ${pagina} OFFSET ${nro_pagina};
