mapper.update:
UPDATE plan_pei
SET id_estado='X'
WHERE id_plan_pei=#{id_plan_pei};

mapper.select:
SELECT id_plan_pei, gestion, descripcion, id_estado
FROM plan_pei
WHERE id_plan_pei=#{id_plan_pei} limit 1;

SELECT id_plan_pei, gestion, descripcion, id_estado
FROM plan_pei
WHERE id_plan_pei=#{id_plan_pei} and id_estado='A';

SELECT id_plan_pei, gestion, descripcion, id_estado
FROM plan_pei
WHERE gestion=#{gestion} and id_estado='A';
